import org.apache.spark.sql.functions._

// NOTE: Update this directory to match where your JVMs Compare Benchmark Results in CSV format are located.
val project_dir = "/Users/cguntur/projects/jvms-compare"
val benchmark_dir = project_dir + "/benchmark-results"
println("Using benchmark results from: " + benchmark_dir)

// NOTE: Update this list if there are other or different JDKs being compared.
val jdks = List("OracleJDK8", "GraalEE", "GraalCE", "AdoptOpenJDK8Hotspot", "AdoptOpenJDKOpenJ9", "OpenJDK11Hotspot", "OpenJDK11Graal", "GraalEEC2")

//NOTE: This was a run with 1k element size. Alter value if a different size is used during the benchmark execution.
val size = "1 Thousand"

// Use the project's dummy.csv to create a dataframe with the right columns. This csv file has no data.
var dummyData = spark.read.option("header", "true").csv(project_dir + "/" + "dummy.csv")

// Create a "sink" dataframe with no data, but right columns, so each benchmark-result can be merged onto this dataframe
var dataFrame = dummyData.toDF("Benchmark", "Mode", "Threads", "Samples", "Score", "Error", "Units", "JDK", "Size")

// Iterate over the 01 through 08 numbered result CSV files where each prefixed number represents a run of the benchmarks with a specific JDK
// NOTE: see the val 'jdks' that map to these numbers
for (i <- 1 to 8) {

    // Iterate over every CSV in each directory under benchmark-results for the current index (index represents a JDK)
    var csvFile = benchmark_dir+"/*/0" + i + "*.csv"
    var benchmarkData = spark.read.option("header", "true").csv(csvFile)

    // Extract direct columns from the CSV data
    var df = benchmarkData.toDF("Benchmark", "Mode", "Threads", "Samples", "Score", "Error", "Units")

    // --- BEGIN: DATA NOT FROM CSV ---
    // Add a JDK and a Size column. Trim the Benchmark name.
    // --------------------------------

    // Add a JDK column with values that are sequentially listed in the jdks val above. 
    // JDKs map to the prefixed number in the results.
    // For instance, OracleJDK8 is the first run and its prefix for results is 01.
    df = df.withColumn("JDK", when($"Mode".isNull, "").otherwise(jdks(i - 1)))

    // Add a Benchmark column that extracts its value from the CSV Benchmark column
    df = df.withColumn("Benchmark", substring($"Benchmark", 23, 200))

    // Add a size column that holds the size of the data tested in the benchmark. Default value in the note is '1 Thousand'.
    // NOTE: see the val 'size' that determines the volume tested per becnmark run.
    df = df.withColumn("Size", when($"Mode".isNull, "").otherwise(size))

    // ------------------------------
    // --- END: DATA NOT FROM CSV ---

    // Merge the data collected from each CSV into the "sink" dataframe
    dataFrame = dataFrame.union(df)
}

// Generate (or replace) a temporary view with the name 'benchmarks' that can now be queried.
dataFrame.createOrReplaceTempView("benchmarks")
